    };

    freebase.mqlread(query, options, cleanup);
  });
};

exports.fetchRoles = function (contributor, track) {
  var query = JSON.stringify({
    mid: track,
    type: '/music/track',
    contributions: [{
      mid: null,
      role: [{
        name: null
      }],
      contributor: {
        mid: contributor
      }
    }]
  });

  return new Promise(function (fulfill, reject) {
    var cleanup = function (err, data) {
      if (err) {
        return reject(err);
      }
      var rv = [];
      var contributions = _.result(data.result, 'contributions');
      var roles = _.map(contributions, function (contribution) {
        return _.map(contribution.role, function (role) {
          return role;
        });
      });
      roles = _.flatten(roles);
      rv.roles = _.uniq(roles, false, function (value) { return value.name; });
      return fulfill(rv);
    };

    freebase.mqlread(query, options, cleanup);
  });
};

exports.getArtistsAndContributorsFromTracks = function (mids) {
  var query = JSON.stringify([{
    'mid|=': mids,
    type: '/music/track',
    artist: [{
      mid: null,
      limit: limit
    }],
    contributions: [{
      mid: null,
      role: [{
        name: null
      }],
      contributor: {
        mid: null
      },
      limit: limit,
      optional: 'optional'
    }]
  }]);

  return new Promise(function (fulfill, reject) {
    var cleanup = function (err, data) {
      if (err) {
        return reject(err);
      }
      var rv = {artists: [], contributors: []};
      _.forEach(data.result, function (value) {
        _.forEach(value.artist, function (value) {
          rv.artists.push({
            mid: _.result(value, 'mid')
          });
        });

        rv.contributors = _.map(value.contributions, function (value) {
          return {
            mid: _.result(value.contributor, 'mid'),
            roles: _.result(value, 'role')
          };
        });
      });

      fulfill(rv);
    };

    freebase.mqlread(query, options, cleanup);
  });
};

exports.getArtistDetails = function (mid) {
