exports.getTrackDetails = function (mid) {
  var query = JSON.stringify({
    mid: mid,
    type: '/music/track',
    name: null,
    artist: [{
      mid: null,
      name: null
    }],
    tracks: [{
      release: {
        mid: null,
        name: null
      },
    }]
  });

  return new Promise(function (fulfill, reject) {
    var cleanup = function (err, data) {
      if (err) {
        return reject(err);
      }
      var rv = null;
      if (data && data.result) {
        rv = {};
        rv.mid = mid;
        rv.name = data.result.name;
        rv.artists = data.result.artist;
        rv.releases = [];
